export default function TaskFilter({ value, onChange }) {
  return (
    <div className="filters" role="tablist" aria-label="Task filter">
      {["all", "completed", "pending"].map(v => (
        <button
          key={v}
          role="tab"
          aria-selected={value === v}
          className={value === v ? "active" : ""}
          onClick={() => onChange(v)}
        >
          {v[0].toUpperCase() + v.slice(1)}
        </button>
      ))}
    </div>
  );
}
